package com.hibernate.demo.entity;
// annotate the class as an entity and map to db table
@Entity
@Table(name="course")
public class Course {
  // define the fields
  // annotate the fields with db column names
  @Id
  @GeneratedValue(strategy=GenerationType.IDENTITY)
  @Column(name="id")
  private int id;

  @Column(name="title")
  private String title;

  // Set up one to many relationship
  @ManyToOne(cascade= 
              // On delete course, do not delete instructor
              {CascadeType.PERSIST, 
              CascadeType.MERGE,
              CascadeType.DETACH, 
              CascadeType.REFRESH})
  @JoinColumn(name="instructor_id")
  private Instructor instructor;

  // Set up unidirectional one to many relationship
  @OneToMany(fetch=FetchType.LAZY, cascade=CascadeType.ALL)
	@JoinColumn(name="course_id")
	private List<Review> reviews;

  // Set up many to many relationship with lazy loading
  // so only Courses are retrieved, and the students associated
  // are obtained only if needed
	@ManyToMany(fetch=FetchType.LAZY,
			cascade= {CascadeType.PERSIST, CascadeType.MERGE,
			 CascadeType.DETACH, CascadeType.REFRESH})
  // Specifying the join table, and the corresponding 
  // foreign keys
  @JoinTable(
    // table name
    name="course_student",
    // this entity's pk
    joinColumns=@JoinColumn(name="course_id"),
    // related entity's pk
    inverseJoinColumns=@JoinColumn(name="student_id")
  )
  private List<Student> students;

  public Course() {
  }		

  ...
  // Setters and getters
}



